private: // скроем детали реализации struct dummy; static dummy detect(...); template<typename U> static decltype(std::declval<U>().foo(42)) detect(const U&); public: static constexpr bool value = std::is_same<void, decltype(detect(std::declval<T>()))>::value;
}; template <typename T>
